import { StyleSheet, Text, TouchableOpacity, View } from 'react-native'
import React from 'react'
import reusable from './reusable.styles'
import { AntDesign } from "@expo/vector-icons"
import ReusableText from './ReusableText'
import { COLORS, TEXT } from '../../constants/theme'

const AppBar = ({color, title, color1, icon, onPress, onPress1}) => {
  return (
    <View style={styles.overlay}>
        <View style={reusable.rowWithSpace("space-between")}>
            <TouchableOpacity style={styles.box(color)} onPress={onPress}>
                <AntDesign name="left" size={26} />
            </TouchableOpacity>
            <ReusableText 
                text={title}
                family={"medium"}
                size={TEXT.large}
                color={COLORS.black}
            />
            <TouchableOpacity style={styles.box1(color1)} onPress={onPress1}>
                <AntDesign name={icon} size={26} />
            </TouchableOpacity>
        </View>
    </View>
  )
}

const styles = StyleSheet.create({
    overlay: {
        position: "absolute",
        top: 60,
        left: 20,
        right: 20,
        justifyContent: "center",
    },
    box: (color) => ({
        backgroundColor: color,
        width: 30,
        height: 30,
        borderRadius: 9,
        alignItems: "center",
        justifyContent: "center",
    }),
    box1: (color1) => ({
        backgroundColor: color1,
        width: 30,
        height: 30,
        borderRadius: 9,
        alignItems: "center",
        justifyContent: "center",
    }),
})

export default AppBar;
